INSERT INTO `Recipes` VALUES (513,'Hummus czekoladowy ','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/hummus-czekoladowy?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Hummus czekoladowy " data-layout="button_count"></div>
