INSERT INTO `Recipes` VALUES (611,'Kompot jabłkowy ','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/kompot-jablkowy?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Kompot jabłkowy " data-layout="button_count"></div>
