INSERT INTO `Recipes` VALUES (1198,'Marchewkowe smoothie z mango i imbirem','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/marchewkowe-smoothie-z-mango-i-imbirem?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Marchewkowe smoothie z mango i imbirem" data-layout="button_count"></div>
