INSERT INTO `Recipes` VALUES (1199,'Brzoskwiniowe smoothie z arbuzem i winogronami','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/brzoskwiniowe-smoothie-z-arbuzem-i-winogronami?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Brzoskwiniowe smoothie z arbuzem i winogronami" data-layout="button_count"></div>
