INSERT INTO `Recipes` VALUES (1201,'Karaibskie smoothie owocowe z sałatą ','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/karaibskie-smoothie-owocowe-z-salata?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Karaibskie smoothie owocowe z sałatą " data-layout="button_count"></div>
