INSERT INTO `Recipes` VALUES (1202,'Szpinakowe smoothie z morelami','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/szpinakowe-smoothie-z-morelami?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Szpinakowe smoothie z morelami" data-layout="button_count"></div>
