INSERT INTO `Recipes` VALUES (1203,'Truskawkowo-ogórkowy lassi ','<div class="article" id="opis">
<div class="fb-share-button right" data-href="/truskawkowo-ogorkowy-lassi?utm_source=facebook.com&amp;utm_medium=kuchniaLike&amp;utm_campaign=Truskawkowo-ogórkowy lassi " data-layout="button_count"></div>
